(48, 'Kepengurusan', '<p style=\"text-align:center\"><span style=\"font-size:14px\"><strong>PENGURUS KAMI UMN&nbsp;PERIODE 2013/2017</strong></span></p>\r\n\r\n<p><strong>Pembina&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>Ika Yanuarti, S.E., MSF.<br />\r\n&nbsp;M. Rizaldi, S.T., M.Ds.<br />\r\nCitrandika KRisandua Okta Selarosa, S.Pd., M.A.<br />\r\nPutri Ayu Rezkiyana, S.S.</p>\r\n\r\n<p><strong>Ketua</strong><br />\r\nBonaventura Aditya Anton Prabowo</p>\r\n\r\n<p><strong>Wakil Ketua</strong><br />\r\nFakhriy Dinansyah<br />\r\n<br />\r\n<strong>Bendahara</strong><br />\r\nGlen Reinner<br />\r\nMaria Stefanie Osesoga<br />\r\n<br />\r\n<strong>Sekretaris</strong><br />\r\nAgatha Maisie Tjandra<br />\r\n<br />\r\n<strong>Divisi Humas</strong></p>\r\n\r\n<p><strong>Humas Internal</strong><br />\r\nRizky Fitria<br />\r\nMaria Puspasari Perdana</p>\r\n\r\n<p><strong>Humas Eksternal</strong><br />\r\nStefanie<br />\r\nMary Daisy T.</p>\r\n', '-', '2016-08-02', 'kepengurusan', '1e113032737de195d185d6ee07ce37d36eb92192.jpg'),
